$(document).ready(function(){
	
	$('a.has-checkbox').siblings('input').prop("checked", true);
	
	// Added 8/4/2013 - Check if footable tab is changed, if is trigger resize event.
	if($('.footable').length > 0) {
		$('.widget-box [data-toggle="tab"]').on('click',function(){
			// Fake loading
			window.setTimeout(function(){ $('.active #loading').show(); },10);
			window.setTimeout(function(){ $('.active #loading').fadeOut(); }, 1000);
			// Trigger resize func
			window.setTimeout(function(){
				$('.tab-pane.active .footable').trigger('resize');
			},20);
		});
	}
	
});